    private void jbtnAddEmpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnAddEmpActionPerformed
       if(validateInputs()==false)
       {
           JOptionPane.showMessageDialog(null, "Please fill All the Values!", "Empty value", JOptionPane.ERROR_MESSAGE);
           return;
       }
       
      try{
       EmpPojo e = new EmpPojo();
       e.setEmpid(jtxtId.getText());
       e.setEmpName(jtxtName.getText());
       e.setJob(jcmJob.getSelectedItem().toString());
       e.setSal(Double.parseDouble(jtxtSal.getText()));
       boolean result= EmpDao.addEmployee(e);
       if(result == true)
       {
          JOptionPane.showMessageDialog(null, "Record Saved Successfully!", "Success", JOptionPane.INFORMATION_MESSAGE);
          jtxtId.removeAll();
          newEmpid();
       }
       else
       {
          JOptionPane.showMessageDialog(null, "Could not Add Employee!", "Error!", JOptionPane.ERROR_MESSAGE);
       }
      }
      catch(SQLException xe)
      {
          JOptionPane.showMessageDialog(null, "Please Provide Numeric Inputs!"+xe, "Error!", JOptionPane.ERROR_MESSAGE);
      } 
    }//GEN-LAST:event_jbtnAddEmpActionPerformed

