private void newEmpid()
{
    jtxtName.setText("");
    jtxtSal.setText("");
    jcmJob.setSelectedIndex(0);
    try
    {
        String str = EmpDao.getNewid();
        jtxtId.setText(str);
    }
    catch(SQLException e)
    {
      JOptionPane.showMessageDialog(null, "Error in the database!"+e, "Error!", JOptionPane.ERROR_MESSAGE);
      e.printStackTrace();
    }
}
private boolean validateInputs()
{
    if(jtxtName.getText().isEmpty()||jtxtSal.getText().isEmpty()) 
        return false;
    return true;
}
